// to do list

import React ,{ useState } from "react";
import Todolist from "./Todolist";

const App = () => {
  const [iList , setiList] = useState("");
  const [Items , setItems] = useState([]);

  const itemEvent = (event) => {
    setiList(event.target.value);
  }

  const listitems = () =>{
    setItems((oldItems) => {
      return [...oldItems , iList];
       
    });
    setiList(" ");
  };

  const deleteItems = (id) => {
    console.log("deleted");

    setItems( (oldItems) => {
      return oldItems.filter((arrElem, index) => {
        return index !==  id;
      });
    });
};

  return(
<>
  <div className="main_div">
  <div className="center_div"> 
  <br />
  <h1> TO DO LIST </h1>
  <br />
  <input type="text" placeholder="Add a Item" value={ iList} onChange={itemEvent}/>
  <button onClick={listitems}>  + </button>
   <ol>
   { 
    Items.map( (itemval,index) => {
      return <Todolist key= {index} id={ index} text = { itemval} onSelect={deleteItems} />;
    })
   }
   </ol>
  </div>
  </div>
</>
  );
};

export default App;
